// src/components/Home.jsx
import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import "./Home.css";

const Home = () => {
  const [eventos, setEventos] = useState([]);

  const vincularEmpresa = () => {
    const reservasFicticias = [
      {
        title: "Reunión con TechCorp",
        start: "2025-04-22T10:00:00",
        end: "2025-04-22T11:00:00",
        color: "#3b82f6",
      },
      {
        title: "Llamada con MarketZone",
        start: "2025-04-23T14:00:00",
        end: "2025-04-23T15:00:00",
        color: "#f59e0b",
      },
      {
        title: "Estrategia con InnovateX",
        start: "2025-04-24T09:30:00",
        end: "2025-04-24T10:30:00",
        color: "#10b981",
      },
    ];
    setEventos(reservasFicticias);
  };

  return (
    <div className="home-container">
      <h2>Reservas de Salas</h2>

      <button className="vincular-btn" onClick={vincularEmpresa}>
        Vincular empresa
      </button>

      <div className="calendar-section">
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={eventos}
          height="auto"
        />
      </div>

      <form className="reserva-form">
        <h3>Reservar una sala</h3>

        <label>Nombre de la sala</label>
        <input type="text" placeholder="Ej: Sala 1" />

        <label>Fecha</label>
        <input type="date" />

        <label>Hora</label>
        <input type="time" />

        <label>Duración (minutos)</label>
        <input type="number" placeholder="Ej: 60" />

        <button type="submit">Reservar</button>
      </form>
    </div>
  );
};

export default Home;
